import { DiscordSDK } from '@discord/embedded-app-sdk';

type ConfigOptions = {
    clientId: string;
    scope: ScopeArgument;
    tokenRoute: `/${string}`;
};
type ScopeArgument = Parameters<DiscordSDK["commands"]["authorize"]>[0]["scope"];

declare function setupSdk(options: ConfigOptions): Promise<void>;

export { setupSdk };
